// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Escrow.sol";

contract EscrowFactory {
    address public owner;
    Escrow[] public escrows;

    mapping(address => address) public escrowToClient;
    mapping(address => address) public escrowToFreelancer;

    event EscrowCreated(address indexed escrowAddress, address indexed client, address indexed freelancer);

    constructor() {
        owner = msg.sender;
    }

    // Pass both client and freelancer as arguments
    function createEscrow(address _client, address _freelancer) external returns (address) {
        Escrow escrow = new Escrow(_client, _freelancer);
        escrows.push(escrow);
        escrowToClient[address(escrow)] = _client;
        escrowToFreelancer[address(escrow)] = _freelancer;
        emit EscrowCreated(address(escrow), _client, _freelancer);
        return address(escrow);
    }

    function getAllEscrows() external view returns (address[] memory) {
        address[] memory addresses = new address[](escrows.length);
        for (uint i = 0; i < escrows.length; i++) {
            addresses[i] = address(escrows[i]);
        }
        return addresses;
    }

    function getEscrowCount() external view returns (uint) {
        return escrows.length;
    }

    function getClient(address escrowAddr) external view returns (address) {
        return escrowToClient[escrowAddr];
    }

    function getFreelancer(address escrowAddr) external view returns (address) {
        return escrowToFreelancer[escrowAddr];
    }
}
